import React, {
  useState
} from 'react';

import './App.css';
import Box from "./components/Box"
import Board from "./components/Board"

function App() {
  const [board, setBoard] = useState(Array(9).fill(null));

  const handleBoxClicked = (boxIdx) => {
    const updateBoard = board.map((value, idx) => {
      if (idx === boxIdx) {
        return "X";
      } else {
        return value;
      }
    })
    setBoard(updateBoard);
  };

  return ( <
    div className = "App" >
    <
    Board board = {
      board
    }
    onClick = {
      handleBoxClicked
    }
    />

    <
    /div>
  );
}

export default App;
